import React, { Component } from "react";
import { connect } from "react-redux";
import MovieCard from "./MovieCard";
import Nodata from "../layout/Nodata";
import ResultTotal from "../home/ResutsTotal";

export class MoviesContainer extends Component {
  render() {
    const { flims } = this.props;

    let content = "";
    content = flims ? (
      flims.map((movie, index) => <MovieCard movie={movie} key={index} />)
    ) : (
      <Nodata />
    );

    let impression = "";
    if (flims.Response === "True") {
      impression = <ResultTotal result={flims.totalResults} />;
    }

    return (
      <div>
        <div className="mt-5">{impression}</div>
        <div className="row">{content}</div>
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  flims: state.movies.movies,
});

export default connect(mapStateToProps)(MoviesContainer);
